	RealType function sumup(CCloop, CCtree)
	implicit none

#include "vars.h"

	ComplexType CCloop(1), CCtree(1)
	ComplexType m

	sumup = 0

	m = 0
	m = m + CCloop(1)*1
	sumup = sumup + Re(Conjugate(CCtree(1))*m)
	end

